/**
 * This is Custom Widget file 
 * 
 * It displays Banner Widget of elementor page builder
 * 
 */

 if ( ! defined( 'ABSPATH' ) ) {
	exit; // Exit if accessed directly.
}

use Elementor\Controls_Manager;
use Elementor\Widget;


class Cl_banner extends Elementor\Widget_Base{
    public function get_name(){
        return 'chuijhal_widget_banner';
    }

    public function get_title(){
        return 'Chuijhal Banner';
    }

    public function get_icon(){
        return 'eicon-menu-card';
    }

    public function get_categories() {
        return ['chuijhal'];
    }

    // ========================== Widget Settings =====================================================================
    protected function register_controls(){
        // Starting the controls section
        $this -> start_controls_section(
            'top_banner',
            [
                'label'     => 'Top Banner',
            ]
        );
        // ============= Inseting Controlls ============
            $this -> add_control(
                'logo',
                [
                    'label'     => 'Logo',
                    'type'      => \Elementor\Controls_Manager::MEDIA,
                    'default'   => [
                                    'url' => plugins_url( '../img/logo.png', __FILE__ )
                                ],
                ]
            );
            $this -> add_control(
                'title',
                [
                    'label'     => 'Title',
                    'type'      => \Elementor\Controls_Manager::TEXT,
                    'default'   => 'The Title',
                ]
            );
            $this -> add_control(
                'description',
                [
                    'label'     => 'Description',
                    'type'      => \Elementor\Controls_Manager::TEXT,
                    'default'   => 'Your Description',
                ]
            );
            $this->add_control(
                'order_now_btn',
                [
                    'label' => esc_html__( 'Link', 'chuijhal' ),
                    'type' => \Elementor\Controls_Manager::URL,
                    'options' => [ 'url', 'is_external', 'nofollow' ],
                    'default' => [
                        'url' => '',
                        'is_external' => false,
                        'nofollow' => true,
                        // 'custom_attributes' => '',
                    ],
                    'label_block' => true,
                ]
            );
            $this -> add_control(
                'banner_bg',
                [
                    'label'     => 'Background image',
                    'type'      => \Elementor\Controls_Manager::MEDIA,
                    'default'   => [
                                    'url' => plugins_url( '../img/chuijhal3.jpeg', __FILE__ )
                                ],
                ]
            );
            $this -> add_control(
                'slider_bg',
                [
                    'label'     => 'Slider image',
                    'type'      => \Elementor\Controls_Manager::MEDIA,
                    'default'   => [
                                    'url' => plugins_url( '../img/chuijhal2.jpg', __FILE__ )
                                ],
                ]
            );


        // let't end the controls section
        $this->end_controls_section();
    }





